mapper.select:
select ID,USERID,ZBID,XH,FSSJ,SCORE,NOTE,LRRBS,LRSJ,ZT from YJKH_KGSS

  		   and ID = #{id} 
  		   and USERID = #{userid} 
  		   and ZBID = #{zbid} 
  		   and XH = #{xh} 
  		   and FSSJ = #{fssj} 
  		   and SCORE = #{score} 
  		   and NOTE = #{note} 
  		   and LRRBS = #{lrrbs} 
  		   and LRSJ = #{lrsj} 
  		   and ZT = #{zt}

select count(*) from YJKH_KGSS

  		   and ID = #{id} 
  		   and USERID = #{userid} 
  		   and ZBID = #{zbid} 
  		   and XH = #{xh} 
  		   and FSSJ = #{fssj} 
  		   and SCORE = #{score} 
  		   and NOTE = #{note} 
  		   and LRRBS = #{lrrbs} 
  		   and LRSJ = #{lrsj} 
  		   and ZT = #{zt}

mapper.update:
update YJKH_KGSS
USERID = #{userid}, ZBID = #{zbid}, XH = #{xh}, FSSJ = #{fssj}, SCORE = #{score}, NOTE = #{note}, LRRBS = #{lrrbs}, LRSJ = #{lrsj}, ZT = #{zt}
where ID = #{id}
